INSERT INTO `sys_oper_log` (`oper_id`, `title`, `business_type`, `method`, `request_method`, `operator_type`, `oper_name`, `dept_name`, `oper_url`, `oper_ip`, `oper_location`, `oper_param`, `json_result`, `status`, `error_msg`, `oper_time`, `cost_time`) VALUES (234, '文章内容', 2, 'com.ruoyi.project.portal.article.controller.PortalArticleController.editSave()', 'POST', 1, 'admin', '集团公司', '/portal/article/edit', '127.0.0.1', '内网IP', '{\"articleId\":[\"49\"],\"articleTitle\":[\"遥望洞庭山水翠，白银盘里一青螺\"],\"subhead\":[\"\"],\"categoryId\":[\"32\"],\"source\":[\"唐诗三百首\"],\"description\":[\"\"],\"content\":[\"<p style=\\\"text-align: center;\\\">望洞庭&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">【唐】刘禹锡&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">湖光秋月两相和，潭面无风镜未磨。&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">遥望洞庭山水翠，白银盘里一青螺。</p><p style=\\\"text-align: center; \\\"><br></p><p>中描写了秋夜月光下洞庭湖的优美景色。微波不兴，平静秀美，分外怡人。诗人飞驰想像，以清新的笔调，生动地描绘出洞庭湖水宁静、祥和的朦胧美，勾画出一幅美丽的洞庭山水图。表现了诗人对大自然的热爱，也表现了诗人壮阔不凡的气度和高卓清奇的情致。&nbsp;&nbsp;</p><p>诗从一个“望”字着眼，“水月交融”、“湖平如镜”，是近望所见；“洞庭山水”、“犹如青螺”，是遥望所得。虽都是写望中景象，差异却显而易见。近景美妙、别致；远景迷潆、奇丽。潭面如镜，湖水如盘，君山如螺。银盘与青螺相映，明月与湖光互衬，更觉情景相容、相得益彰。</p>\"],\"artSkip\":[\"\"],\"remark\":[\"\"],\"thumbnail\":[\"\"],\"isPush\":[\"0\"],\"isSlide\":[\"0\"],\"isTop\":[\"0\"],\"tags\":[\"\"]}', '{\"msg\":\"操作成功\",\"code\":0}', 0, NULL, '2024-12-01 15:57:58', 6);
