INSERT INTO `sys_oper_log` (`oper_id`, `title`, `business_type`, `method`, `request_method`, `operator_type`, `oper_name`, `dept_name`, `oper_url`, `oper_ip`, `oper_location`, `oper_param`, `json_result`, `status`, `error_msg`, `oper_time`, `cost_time`) VALUES (246, '文章内容', 2, 'com.ruoyi.project.portal.article.controller.PortalArticleController.editSave()', 'POST', 1, 'admin', '集团公司', '/portal/article/edit', '127.0.0.1', '内网IP', '{\"articleId\":[\"58\"],\"articleTitle\":[\"大江东去，浪淘尽，千古风流人物\"],\"subhead\":[\"\"],\"categoryId\":[\"18\"],\"source\":[\"唐诗三百首\"],\"description\":[\"\"],\"content\":[\"<p style=\\\"text-align: center;\\\">念奴娇·赤壁怀古&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">【宋】苏轼</p><p style=\\\"text-align: center;\\\">大江东去，浪淘尽，千古风流人物。&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">故垒西边，人道是，三国周郎赤壁。&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">乱石穿空，惊涛拍岸，卷起千堆雪。&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">江山如画，一时多少豪杰。&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">遥想公瑾当年，小乔初嫁了，雄姿英发。&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">羽扇纶巾，谈笑间，樯橹灰飞烟灭。&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">故国神游，多情应笑我，早生华发。&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">人生如梦，一尊还酹江月。</p><p style=\\\"text-align: center; \\\"><br></p><p style=\\\"text-align: left;\\\">此词怀古抒情，写自己消磨壮心殆尽，转而以旷达之心关注历史和人生。上阕以描写赤壁矶风起浪涌的自然风景为主，意境开阔博大，感慨隐约深沉。起笔凌云健举，包举有力。将浩荡江流与千古人事并收笔下。千古风流人物既被大浪淘尽，则一己之微岂不可悲？然而苏轼却另有心得：既然千古风流人物也难免如此，那么一己之荣辱穷达复何足悲叹！人类既如此殊途而同归，则汲汲于一时功名，不免过于迂腐了。接下两句切入怀古主题，专说三国赤壁之事。“人道是“三字下得极有分寸。赤壁之战的故地，争议很大。一说在今湖北蒲圻县境内，已改为赤壁市。</p>\"],\"artSkip\":[\"\"],\"remark\":[\"\"],\"thumbnail\":[\"\"],\"isPush\":[\"1\"],\"isSlide\":[\"0\"],\"isTop\":[\"0\"],\"tags\":[\"\"]}', '{\"msg\":\"操作成功\",\"code\":0}', 0, NULL, '2024-12-01 16:06:52', 7);
