INSERT INTO `sys_oper_log` (`oper_id`, `title`, `business_type`, `method`, `request_method`, `operator_type`, `oper_name`, `dept_name`, `oper_url`, `oper_ip`, `oper_location`, `oper_param`, `json_result`, `status`, `error_msg`, `oper_time`, `cost_time`) VALUES (258, '文章内容', 2, 'com.ruoyi.project.portal.article.controller.PortalArticleController.editSave()', 'POST', 1, 'admin', '集团公司', '/portal/article/edit', '127.0.0.1', '内网IP', '{\"articleId\":[\"27\"],\"articleTitle\":[\"等闲识得东风面，万紫千红总是春\"],\"subhead\":[\"\"],\"categoryId\":[\"18\"],\"source\":[\"办公室\"],\"description\":[\"\"],\"content\":[\"<p style=\\\"text-align: center;\\\">春日&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">【宋】朱熹</p><p style=\\\"text-align: center;\\\">胜日寻芳泗水滨，无边光景一时新。&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">等闲识得东风面，万紫千红总是春。&nbsp;&nbsp;</p><p><br></p><p><br></p><p>人们一般都认为这是一首咏春。从诗中所写的景物来看，也很像是这样。首句“胜日寻芳泗水滨”，“胜日”指晴日，点明天气。“泗水滨”点明地点。“寻芳”，即是寻觅美好的春景，点明了主题。下面三句都是写“寻芳”所见所得。次句“无边光景一时新”，写观赏春景中获得的初步印象。用“无边”形容视线所及的全部风光景物。“一时新”，既写出春回大地，自然景物焕然一新，也写出了作者郊游时耳目一新的欣喜感觉。第三句“等闲识得东风面”，句中的“识”字承首句中的“寻”字。“等闲识得”是说春天的面容与特征是很容易辨认的。“东风面”借指春天。</p>\"],\"artSkip\":[\"\"],\"remark\":[\"\"],\"thumbnail\":[\"\"],\"isPush\":[\"0\"],\"isSlide\":[\"0\"],\"isTop\":[\"0\"],\"tags\":[\"\"]}', '{\"msg\":\"操作成功\",\"code\":0}', 0, NULL, '2024-12-01 16:20:31', 4);
