INSERT INTO `sys_oper_log` (`oper_id`, `title`, `business_type`, `method`, `request_method`, `operator_type`, `oper_name`, `dept_name`, `oper_url`, `oper_ip`, `oper_location`, `oper_param`, `json_result`, `status`, `error_msg`, `oper_time`, `cost_time`) VALUES (269, '文章内容', 2, 'com.ruoyi.project.portal.article.controller.PortalArticleController.editSave()', 'POST', 1, 'admin', '集团公司', '/portal/article/edit', '127.0.0.1', '内网IP', '{\"articleId\":[\"8\"],\"articleTitle\":[\"国破山河在，城春草木深\"],\"subhead\":[\"国破山河在，城春草木深\"],\"categoryId\":[\"17\"],\"source\":[\"百度百科\"],\"description\":[\"s\"],\"content\":[\"<p style=\\\"text-align: center;\\\">春望&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">【唐】杜甫</p><p style=\\\"text-align: center;\\\">国破山河在，城春草木深。&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">感时花溅泪，恨别鸟惊心。&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">烽火连三月，家书抵万金。&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">白头搔更短，浑欲不胜簪。</p><p style=\\\"text-align: center; \\\"><br></p><p style=\\\"text-align: left;\\\">“国破山河在，城春草木深。”篇一开头描写了春望所见：山河依旧，可是国都已经沦陷，城池也在战火中残破不堪了，乱草丛生，林木荒芜。诗人记忆中昔日长安的春天是何等的繁华，鸟语花香，飞絮弥漫，烟柳明媚，游人迤逦，可是那种景象今日已经荡然无存了。一个“破”字使人怵目惊心，继而一个“深”字又令人满目凄然。诗人写今日景物，实为抒发人去物非的历史感，将感情寄寓于物，借助景物反托情感，为全诗创造了一片荒凉凄惨的气氛。“国破”和“城春”两个截然相反的意象，同时存在并形成强烈的反差。“城春”当指春天花草树木繁盛茂密，烟景明丽的季节，可是由于“国破”。</p>\"],\"artSkip\":[\"\"],\"remark\":[\"\"],\"thumbnail\":[\"\"],\"isIssue\":[\"1\"],\"isPush\":[\"1\"],\"isSlide\":[\"0\"],\"isTop\":[\"0\"],\"tags\":[\"\"]}', '{\"msg\":\"操作成功\",\"code\":0}', 0, NULL, '2024-12-01 16:35:24', 7);
INSERT INTO `sys_oper_log` (`oper_id`, `title`, `business_type`, `method`, `request_method`, `operator_type`, `oper_name`, `dept_name`, `oper_url`, `oper_ip`, `oper_location`, `oper_param`, `json_result`, `status`, `error_msg`, `oper_time`, `cost_time`) VALUES (270, '文章内容', 2, 'com.ruoyi.project.portal.article.controller.PortalArticleController.editSave()', 'POST', 1, 'admin', '集团公司', '/portal/article/edit', '127.0.0.1', '内网IP', '{\"articleId\":[\"10\"],\"articleTitle\":[\"清明时节雨纷纷，路上行人欲断魂。 \"],\"subhead\":[\"6\"],\"categoryId\":[\"17\"],\"source\":[\"s\"],\"description\":[\"\"],\"content\":[\"<p style=\\\"text-align: center;\\\">清明&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">【唐】杜牧&nbsp;&nbsp;</p><p style=\\\"text-align: center;\\\">清明时节雨纷纷，路上行人欲断魂。&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">借问酒家何处有？牧童遥指杏花村。&nbsp;&nbsp;</p><p><br></p><p><br></p><p>这一天正是清明佳节。诗人杜牧，在行路中间，可巧遇上了雨。清明，虽然是柳绿花红、春光明媚的时节，可也是气候容易发生变化的期间，常常赶上“闹天气”。远在梁代，就有人记载过：在清明前两天的寒食节，往往有“疾风甚雨”。若是正赶在清明这天下雨，还有个专名叫作“泼火雨”。诗人杜牧遇上的，正是这样一个日子。&nbsp;&nbsp;</p><p>诗人用“纷纷”两个字来形容那天的“泼火雨”，真是好极了。怎见得呢？“纷纷”，若是形容下雪，那该是大雪，所谓“纷纷扬扬，降下好一场大雪来”。但是临到雨，情况却正相反，那种叫人感到“纷纷”的，绝不是大雨，而是细雨。</p>\"],\"artSkip\":[\"\"],\"remark\":[\"\"],\"thumbnail\":[\"\"],\"isIssue\":[\"1\"],\"isPush\":[\"0\"],\"isSlide\":[\"0\"],\"isTop\":[\"0\"],\"tags\":[\"\"]}', '{\"msg\":\"操作成功\",\"code\":0}', 0, NULL, '2024-12-01 16:36:55', 13);
