INSERT INTO `sys_oper_log` (`oper_id`, `title`, `business_type`, `method`, `request_method`, `operator_type`, `oper_name`, `dept_name`, `oper_url`, `oper_ip`, `oper_location`, `oper_param`, `json_result`, `status`, `error_msg`, `oper_time`, `cost_time`) VALUES (272, '文章内容', 1, 'com.ruoyi.project.portal.article.controller.PortalArticleController.addSave()', 'POST', 1, 'admin', '集团公司', '/portal/article/add', '127.0.0.1', '内网IP', '{\"articleTitle\":[\"饮湖上初晴后雨二首·其二  \"],\"subhead\":[\"\"],\"categoryId\":[\"23\"],\"source\":[\"唐诗三百首\"],\"description\":[\"\"],\"content\":[\"<p style=\\\"text-align: center; \\\">饮湖上初晴后雨二首·其二&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">【宋】苏轼&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">水光潋滟晴方好，山色空蒙雨亦奇。&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">欲把西湖比西子，淡妆浓抹总相宜。</p><p style=\\\"text-align: center; \\\"><br></p><p style=\\\"text-align: left;\\\">诗的上半首既写了西湖的水光山色，也写了西湖的晴姿雨态。“水光潋滟晴方好”描写西湖晴天的水光：在灿烂的阳光照耀下，西湖水波荡漾，波光闪闪，十分美丽。“山色空蒙雨亦奇”描写雨天的山色：在雨幕笼罩下，西湖周围的群山，迷迷茫茫，若有若无，非常奇妙。从第一首诗可知，这一天诗人陪着客人在西湖游宴终日，早晨阳光明艳，后来转阴，入暮后下起雨来。而在善于领略自然并对西湖有深厚感情的诗人眼中，无论是水是山，或晴或雨，都是美好奇妙的。从“晴方好”“雨亦奇”这一赞评，可以想见在不同天气下的湖山胜景，也可想见诗人即景挥毫时的兴会及其洒脱的性格、开阔的胸怀。</p>\"],\"artSkip\":[\"\"],\"remark\":[\"\"],\"thumbnail\":[\"\"],\"tags\":[\"\"]}', '{\"msg\":\"操作成功\",\"code\":0}', 0, NULL, '2024-12-01 17:30:57', 14);
