INSERT INTO `sys_oper_log` (`oper_id`, `title`, `business_type`, `method`, `request_method`, `operator_type`, `oper_name`, `dept_name`, `oper_url`, `oper_ip`, `oper_location`, `oper_param`, `json_result`, `status`, `error_msg`, `oper_time`, `cost_time`) VALUES (274, '文章内容', 1, 'com.ruoyi.project.portal.article.controller.PortalArticleController.addSave()', 'POST', 1, 'admin', '集团公司', '/portal/article/add', '127.0.0.1', '内网IP', '{\"articleTitle\":[\"举头望明月，低头思故乡。\"],\"subhead\":[\"\"],\"categoryId\":[\"23\"],\"source\":[\"唐诗三百首\"],\"description\":[\"\"],\"content\":[\"<p style=\\\"text-align: center; \\\">静夜思&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">【唐】李白</p><p style=\\\"text-align: center; \\\">床前明月光，疑是地上霜。&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">举头望明月，低头思故乡。</p><p style=\\\"text-align: center; \\\"><br></p><p style=\\\"text-align: left;\\\">这首诗写的是在寂静的月夜思念家乡的感受。诗的前两句，是写诗人在作客他乡的特定环境中一刹那间所产生的错觉。一个独处他乡的人，白天奔波忙碌，倒还能冲淡离愁，然而一到夜深人静的时候，心头就难免泛起阵阵思念故乡的波澜。何况是在月明之夜，更何况是月色如霜的秋夜。“疑是地上霜”中的“疑”字，生动地表达了诗人睡梦初醒，迷离恍惚中将照射在床前的清冷月光误作铺在地面的浓霜。而“霜”字用得更妙，既形容了月光的皎洁，又表达了季节的寒冷，还烘托出诗人飘泊他乡的孤寂凄凉之情。</p>\"],\"artSkip\":[\"\"],\"remark\":[\"\"],\"thumbnail\":[\"\"],\"tags\":[\"\"]}', '{\"msg\":\"操作成功\",\"code\":0}', 0, NULL, '2024-12-01 17:33:51', 8);
