INSERT INTO `sys_oper_log` (`oper_id`, `title`, `business_type`, `method`, `request_method`, `operator_type`, `oper_name`, `dept_name`, `oper_url`, `oper_ip`, `oper_location`, `oper_param`, `json_result`, `status`, `error_msg`, `oper_time`, `cost_time`) VALUES (280, '文章内容', 1, 'com.ruoyi.project.portal.article.controller.PortalArticleController.addSave()', 'POST', 1, 'ry', '办公室', '/portal/article/add', '127.0.0.1', '内网IP', '{\"articleTitle\":[\"小楼昨夜又东风，故国不堪回首月明中\"],\"subhead\":[\"\"],\"categoryId\":[\"22\"],\"source\":[\"\"],\"description\":[\"\"],\"content\":[\"<p style=\\\"text-align: center; \\\">虞美人·春花秋月何时了&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">【五代十国】李煜【朝代】拼音译文&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">春花秋月何时了？往事知多少。小楼昨夜又东风，故国不堪回首月明中。&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">雕栏玉砌应犹在，只是朱颜改。问君能有几多愁？恰似一江春水向东流。</p><p style=\\\"text-align: left;\\\"><br></p><p style=\\\"text-align: left;\\\">《虞美人》李煜的代表作，也是李后主的绝命词。相传他于自己生日（七月七日）之夜（“七夕”），在寓所命歌妓作乐，唱新作《虞美人》词，声闻于外。宋太宗闻之大怒，命人赐药酒，将他毒死。这首词通过今昔交错对比，表现了一个亡国之君的无穷的哀怨。&nbsp;&nbsp;</p><p style=\\\"text-align: left;\\\">“春花秋月何时了，往事知多少！”三春花开，中秋月圆，岁月不断更替，人生多么美好。可我这囚犯的苦难岁月，什么时候才能完结呢？“春花秋月何时了”表明词人身为阶下囚，怕春花秋月勾起往事而伤怀。回首往昔，身为国君，过去许许多多的事到底做得如何呢，怎么会弄到今天这步田地？</p>\"],\"artSkip\":[\"\"],\"remark\":[\"\"],\"thumbnail\":[\"\"],\"tags\":[\"\"]}', '{\"msg\":\"操作成功\",\"code\":0}', 0, NULL, '2024-12-01 17:42:13', 25);
