INSERT INTO `sys_oper_log` (`oper_id`, `title`, `business_type`, `method`, `request_method`, `operator_type`, `oper_name`, `dept_name`, `oper_url`, `oper_ip`, `oper_location`, `oper_param`, `json_result`, `status`, `error_msg`, `oper_time`, `cost_time`) VALUES (282, '文章内容', 1, 'com.ruoyi.project.portal.article.controller.PortalArticleController.addSave()', 'POST', 1, 'ry', '办公室', '/portal/article/add', '127.0.0.1', '内网IP', '{\"articleTitle\":[\"一个深夜的记忆\"],\"subhead\":[\"\"],\"categoryId\":[\"23\"],\"source\":[\"百度百科\"],\"description\":[\"\"],\"content\":[\"<p style=\\\"text-align: center; \\\">一个深夜的记忆&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">【作者】鲁藜【朝代】现代</p><p style=\\\"text-align: center; \\\">月光流进门槛&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">我以为是阳光&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">开门，还是深夜&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">不久，有风从北边来&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">仿佛吹动了月亮的弓弦&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">于是我听见了黎明的音响&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">河岸被山影压着&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">有星流过旷野去&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">我感觉到，万物还在沉睡&nbsp;&nbsp;</p><p style=\\\"text-align: center; \\\">只有我是最初醒来的人</p><p style=\\\"text-align: center; \\\"><br></p><p style=\\\"text-align: center; \\\"><br></p><p style=\\\"text-align: left;\\\">宁静而深沉的意境&nbsp;&nbsp;</p><p style=\\\"text-align: left;\\\">曾卓&nbsp;&nbsp;</p><p style=\\\"text-align: left;\\\">人们谈论鲁藜的诗，一般都没有提到这一首，而我对它却很有偏爱。&nbsp;&nbsp;</p><p style=\\\"text-align: left;\\\">这首诗写于抗战时期的延安，描写的是诗人在一个深夜醒来时所看到的情景。&nbsp;&nbsp;</p><p style=\\\"text-align: left;\\\">从门槛下流进的月光，诗人误以为是曙光。从梦中乍然醒来，是容易有这样的错觉的，李白就曾将床前的月光误以为是霜。他起来了，推开门看看，原来还是深夜。可以想见，他是习惯于黎明即起的。是他惊醒的是对黎明的期待。</p>\"],\"artSkip\":[\"\"],\"remark\":[\"\"],\"thumbnail\":[\"\"],\"tags\":[\"\"]}', '{\"msg\":\"操作成功\",\"code\":0}', 0, NULL, '2024-12-01 17:44:33', 21);
