INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (7, 100, '单位介绍', '', 101, '', 's', '', '<p>　　青海青一色电子科技有限公司成立于2013年8月27日，注册资金200万，坐落于青海西宁市，是一家专业软件定制开发科研型公司，不限于小程序/APP开发、网站建设、应用业务系统、物联网开发等业务领域。以设计、研发、运维于一体立足于互联网行业，其业务覆盖青海省及全国；以诚信经营、完善售后赢得顾客信赖；以定制开发、软硬件适配、技术运维服务等多种配套经营模式上门服务，赢得了广大客户的一致好评！</p><p><br></p><p>　　公司自创办至今,一贯以“严要求、高品味、高标准”为方向定位;“以质求胜、诚实信用、热忱服务、互利双赢”为经营宗旨;以“追求卓越、超越自我”为经营理念;以务实的信念全情投入到经营发展中去,满足政府、企事单位个性化定制的需求,受到广大顾客的信赖。</p>', '1', '', 0, '0', '0', '0', '0', '0', '0', '0', NULL, 's', 'admin', '2024-05-10 10:39:22', 'admin', '2024-10-19 22:02:58');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (8, 100, '国破山河在，城春草木深', '国破山河在，城春草木深', 17, '百度百科', 's', '', '<p style=\"text-align: center;\">春望&nbsp;&nbsp;</p><p style=\"text-align: center;\">【唐】杜甫</p><p style=\"text-align: center;\">国破山河在，城春草木深。&nbsp;&nbsp;</p><p style=\"text-align: center;\">感时花溅泪，恨别鸟惊心。&nbsp;&nbsp;</p><p style=\"text-align: center;\">烽火连三月，家书抵万金。&nbsp;&nbsp;</p><p style=\"text-align: center; \">白头搔更短，浑欲不胜簪。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">“国破山河在，城春草木深。”篇一开头描写了春望所见：山河依旧，可是国都已经沦陷，城池也在战火中残破不堪了，乱草丛生，林木荒芜。诗人记忆中昔日长安的春天是何等的繁华，鸟语花香，飞絮弥漫，烟柳明媚，游人迤逦，可是那种景象今日已经荡然无存了。一个“破”字使人怵目惊心，继而一个“深”字又令人满目凄然。诗人写今日景物，实为抒发人去物非的历史感，将感情寄寓于物，借助景物反托情感，为全诗创造了一片荒凉凄惨的气氛。“国破”和“城春”两个截然相反的意象，同时存在并形成强烈的反差。“城春”当指春天花草树木繁盛茂密，烟景明丽的季节，可是由于“国破”。</p>', '0', '', 4, '1', '0', '0', '1', '1', '0', '1', NULL, '', 'admin', '2024-05-10 10:39:42', 'admin', '2024-12-01 16:35:24');
