INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (10, 100, '清明时节雨纷纷，路上行人欲断魂。 ', '6', 17, '唐诗三百首', '', '', '<p style=\"text-align: center;\">清明&nbsp;&nbsp;</p><p style=\"text-align: center;\">【唐】杜牧&nbsp;&nbsp;</p><p style=\"text-align: center;\">清明时节雨纷纷，路上行人欲断魂。&nbsp;&nbsp;</p><p style=\"text-align: center; \">借问酒家何处有？牧童遥指杏花村。&nbsp;&nbsp;</p><p><br></p><p><br></p><p>这一天正是清明佳节。诗人杜牧，在行路中间，可巧遇上了雨。清明，虽然是柳绿花红、春光明媚的时节，可也是气候容易发生变化的期间，常常赶上“闹天气”。远在梁代，就有人记载过：在清明前两天的寒食节，往往有“疾风甚雨”。若是正赶在清明这天下雨，还有个专名叫作“泼火雨”。诗人杜牧遇上的，正是这样一个日子。&nbsp;&nbsp;</p><p>诗人用“纷纷”两个字来形容那天的“泼火雨”，真是好极了。怎见得呢？“纷纷”，若是形容下雪，那该是大雪，所谓“纷纷扬扬，降下好一场大雪来”。但是临到雨，情况却正相反，那种叫人感到“纷纷”的，绝不是大雨，而是细雨。</p>', '0', '', 1, '0', '0', '0', '1', '1', '0', '1', NULL, '', 'admin', '2024-05-10 19:48:15', 'admin', '2024-12-01 16:37:05');
