INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (21, 100, '飞流直下三千尺，疑是银河落九天', '', 13, '唐诗三百首', '', '', '<p style=\"text-align: center;\">望庐山瀑布&nbsp;&nbsp;</p><p style=\"text-align: center;\">【作者】李白</p><p style=\"text-align: center;\">日照香炉生紫烟，遥看瀑布挂前川。&nbsp;&nbsp;</p><p style=\"text-align: center; \">飞流直下三千尺，疑是银河落九天。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">这是李白五十岁左右隐居庐山时写的一首风景诗。这首诗形象地描绘了庐山瀑布雄奇壮丽的景色，反映了诗人对祖国大好河山的无限热爱。首句“日照香炉生紫烟”。“香炉”是指庐山的香炉峰。此峰在庐山西北，形状尖圆，像座香炉。由于瀑布飞泻，水气蒸腾而上，在丽日照耀下，仿佛有座顶天立地的香炉冉冉升起了团团紫烟。一个“生”字把烟云冉冉上升的景象写活了。此句为瀑布设置了雄奇的背景，也为下文直接描写瀑布渲染了气氛。</p>', '0', '', 3, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-05-13 10:00:55', 'admin', '2024-12-01 15:39:41');
