INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (24, 100, '出塞二首·其一：秦时明月汉时关，万里长征人未还', '', 13, '办公室', '', '', '<p style=\"text-align: center;\">秦时明月汉时关，万里长征人未还。&nbsp;&nbsp;</p><p style=\"text-align: center; \">但使龙城飞将在，不教胡马度阴山。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \"><br></p><p>　　这是一首著名的边塞，表达了诗人希望起任良将，早日平息边塞战事，使人民过上安定的生活的愿望。</p><p><br></p><p>　　诗人从描写景物景入手，首句勾勒出一幅冷月照边关的苍凉景象。“秦时明月汉时关”不能理解为秦时的明月汉代的关。这里是秦、汉、关、月四字交错使用，在修辞上叫“互文见义”，意思是秦汉时的明月，秦汉时的关。诗人暗示，这里的战事自秦汉以来一直未间歇过，突出了时间的久远。次句“万里长征人未还”，“万里”指边塞和内地相距万里，虽属虚指，却突出了空间辽阔。“人未还”使人联想到战争给人带来的灾难，表达了诗人悲愤的情感。</p><p></p>', '0', '', 7, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-25 15:05:18', 'ry', '2024-09-16 20:39:52');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (25, 100, '僵卧孤村不自哀，尚思为国戍轮台。', '', 13, '办公室', '', '', '<p style=\"text-align: center; \">十一月四日风雨大作（其二）<br></p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \">僵卧孤村不自哀，尚思为国戍轮台。&nbsp;&nbsp;</p><p style=\"text-align: center; \">夜阑卧听风吹雨，铁马冰河入梦来。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">　　风卷江湖雨暗村，四山声作海涛翻。溪柴火软蛮毡暖，我与狸奴不出门。僵卧孤村不自哀，尚思为国戍轮台。夜阑卧听风吹雨，铁马冰河入梦来。第一首诗主要写十一月四日的大雨和诗人之处境。前两句以夸张之法写大雨瓢泼，其声响之巨，描绘出黑天大风大雨之境，很是生动，波涛汹涌之声正与作者渴望为国出力、光复中原之心相印。后两句转写近处，描写其所处之境，写出作者因天冷而不思出门，其妙处是把作者的主观之感和猫结合一起写。第二首诗以“痴情化门”的手法，深沉地表达了作者收复国土、报效祖国的壮志和那种“年既老而不衰”的矢志不渝精神，向读者展示了诗人的一片赤胆忠心<br></p><p></p>', '0', '', 7, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-24 15:05:18', 'ry', '2024-09-17 18:24:58');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (26, 102, '王师北定中原日，家祭无忘告乃翁', '', 13, '办公室', '', '', '<h1 style=\"text-align: center; font-size: 20px; margin-top: 0.67em; margin-bottom: 0.67em; color: rgb(51, 51, 51); padding: 0px 5px; font-family: Arial, sans-serif;\">示儿</h1><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \">死去元知万事空，但悲不见九州同。&nbsp;&nbsp;</p><p style=\"text-align: center; \">王师北定中原日，家祭无忘告乃翁。</p><p style=\"text-align: center; \"><br></p><p>　　是陆游爱国诗中的又一首名篇。陆游一生致力于抗金斗争，一直希望能收复中原。虽然频遇挫折，却仍然未改变初衷。从诗中可以领会到诗人的爱国激情是何等的执着、深沉、热烈、真挚！也凝聚着诗人毕生的心事，诗人始终如一地抱着当时汉民族必然要光复旧物的信念，对抗战事业具有必胜的信心。题目是《示儿》，相当于遗嘱。在短短的篇幅中，诗人披肝沥胆地嘱咐着儿子，无比光明磊落，激动人心！浓浓的爱国之情跃然纸上。</p><p><br></p><p>　　诗人在写此诗的十一年以前，叹息过“死前恨不见中原”，在热烈地期待着旧业的光复。直到临终之际，诗人仍然抱有这样坚定的信念，宋朝的抗敌部队要挥戈</p><p></p>', '0', '', 4, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-23 15:05:18', 'ry', '2024-09-17 18:40:21');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (27, 100, '等闲识得东风面，万紫千红总是春', '', 18, '办公室', '', '', '<p style=\"text-align: center;\">春日&nbsp;&nbsp;</p><p style=\"text-align: center;\">【宋】朱熹</p><p style=\"text-align: center;\">胜日寻芳泗水滨，无边光景一时新。&nbsp;&nbsp;</p><p style=\"text-align: center; \">等闲识得东风面，万紫千红总是春。&nbsp;&nbsp;</p><p><br></p><p><br></p><p>人们一般都认为这是一首咏春。从诗中所写的景物来看，也很像是这样。首句“胜日寻芳泗水滨”，“胜日”指晴日，点明天气。“泗水滨”点明地点。“寻芳”，即是寻觅美好的春景，点明了主题。下面三句都是写“寻芳”所见所得。次句“无边光景一时新”，写观赏春景中获得的初步印象。用“无边”形容视线所及的全部风光景物。“一时新”，既写出春回大地，自然景物焕然一新，也写出了作者郊游时耳目一新的欣喜感觉。第三句“等闲识得东风面”，句中的“识”字承首句中的“寻”字。“等闲识得”是说春天的面容与特征是很容易辨认的。“东风面”借指春天。</p>', '0', '', 1, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-22 15:05:18', 'admin', '2024-12-01 16:20:41');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (28, 100, '故人西辞黄鹤楼，烟花三月下扬州', '', 18, '唐诗三百首', '', '', '<p style=\"text-align: center;\">黄鹤楼送孟浩然之广陵&nbsp;&nbsp;</p><p style=\"text-align: center;\">【唐】李白</p><p style=\"text-align: center;\">故人西辞黄鹤楼，烟花三月下扬州。&nbsp;&nbsp;</p><p style=\"text-align: center;\">孤帆远影碧空尽，唯见长江天际流。&nbsp;&nbsp;</p><p>这是一首送别诗。孟浩然从湖北到广陵去，李白在黄鹤楼给他送行，作了这首诗时间应当在李白出蜀漫游以后。李白从27岁到35岁的将近十年之间，虽然也到处漫游，但却比较固定的居住在今湖北安陆境外，这时，他认识了当时著名的诗人孟浩然，孟浩然比他大11岁，本是襄阳（今属湖北省）人，隐居鹿山门，常在吴、越、湘、闽等地漫游。这时他正想出游吴、越一带，两位大诗人在黄鹤楼分别，留下著名诗篇。诗题中“之广陵”的“之”就是“去”或“往”的意思。</p>', '0', '', 6, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-21 15:05:18', 'admin', '2024-12-01 16:08:30');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (29, 100, '小娃撑小艇，偷采白莲回；不解藏踪迹，浮萍一道开', '', 32, '唐诗三百首', '', '', '<p style=\"text-align: center;\">池上&nbsp;&nbsp;</p><p style=\"text-align: center;\">【唐】白居易【&nbsp;&nbsp;</p><p style=\"text-align: center;\">小娃撑小艇，偷采白莲回。&nbsp;&nbsp;</p><p style=\"text-align: center; \">不解藏踪迹，浮萍一道开。</p><p style=\"text-align: center; \"><br></p><p>这首诗写一个小孩儿偷采白莲的情景。从诗的小主人公撑船进入画面，到他离去只留下被划开的一片浮萍，有景有色，有行动描写，有心理刻画，细致逼真，富有情趣；而这个小主人公的天真幼稚、活泼淘气的可爱形象，也就栩栩如生，跃然纸上了。&nbsp;&nbsp;</p><p>诗人在诗中叙述一个小娃娃生活中的一件小事，准确地捕捉了小娃娃瞬间的心情，勾画出一幅采莲图。莲花盛开的夏日里，天真活泼的儿童，撑着一条小船，偷偷地去池中采摘白莲花玩。兴高采烈地采到莲花，早已忘记自己是瞒着大人悄悄地去的，不懂得或是没想到去隐蔽自己的踪迹，得意忘形地大摇大摆划着小船回来。</p>', '0', '', 2, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-20 15:05:18', 'admin', '2024-12-01 16:00:10');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (30, 100, '天门中断楚江开，碧水东流至此回；两岸青山相对出，孤帆一片日边来', '', 32, '办公室', '', '', '<p style=\"text-align: center;\">望天门山&nbsp;&nbsp;</p><p style=\"text-align: center;\">【唐】李白</p><p style=\"text-align: center;\">天门中断楚江开，碧水东流至此回。&nbsp;&nbsp;</p><p style=\"text-align: center; \">两岸青山相对出，孤帆一片日边来。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">这首诗写了碧水青山，白帆红日，交映成一幅色彩绚丽的画面。但这画面不是静止的，而是流动的。随着诗人行舟，山断江开，东流水回，青山相对迎出，孤帆日边驶来，景色由远及近再及远地展开。诗中用了六个动词“断、开、流、回、出、来”，山水景物呈现出跃跃欲出的动态，描绘了天门山一带的雄奇阔远。一、二句写出了天门山水雄奇险峻不可阻遏的气势，给人惊心动魄之感；三、四句写足也写活了浑阔茫远的水势。</p>', '0', '', 1, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-19 15:05:18', 'admin', '2024-12-01 15:53:53');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (31, 100, '好雨知时节，当春乃发生。随风潜入夜，润物细无声', '', 32, '唐诗三百首', '', '', '<p style=\"text-align: center;\">春夜喜雨&nbsp;&nbsp;</p><p style=\"text-align: center;\">【朝代】杜甫</p><p style=\"text-align: center;\">好雨知时节，当春乃发生。&nbsp;&nbsp;<span style=\"color: inherit;\">随风潜入夜，润物细无声。&nbsp;&nbsp;</span></p><p style=\"text-align: center;\">野径云俱黑，江船火独明。&nbsp;&nbsp;<span style=\"color: inherit;\">晓看红湿处，花重锦官城。</span></p><p style=\"text-align: center;\"><span style=\"color: inherit;\"><br></span></p><p style=\"text-align: left;\">这是描绘春夜雨景，表现喜悦心情的名作。一开头就用一个“好”字赞美“雨”。在生活里，“好”常常被用来赞美那些做好事的人。如今用“好”赞美雨，已经会唤起关于做好事的人的联想。接下去，就把雨拟人化，说它“知时节”，懂得满足客观需要。其中“知”字用得传神，简直把雨给写活了。春天是万物萌芽生长的季节，正需要下雨，雨就下起来了。它的确很“好”。</p>', '0', '', 7, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-18 15:05:18', 'admin', '2024-12-01 15:49:17');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (32, 100, '九月九日忆山东兄弟 ', '', 32, '唐诗三百首', '', '', '<p style=\"text-align: center;\">九月九日忆山东兄弟&nbsp;&nbsp;</p><p style=\"text-align: center;\">【作者】王维&nbsp;&nbsp;</p><p style=\"text-align: center;\">独在异乡为异客，每逢佳节倍思亲。&nbsp;&nbsp;</p><p style=\"text-align: center; \">遥知兄弟登高处，遍插茱萸少一人。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">这是诗人十七岁时在长安所作，少年之作而能戒除浮泛的雕琢，直指真情实感，确实是深知作诗的窍门。诗人的家乡蒲州（今山西永济）在华山之东，所以题为“忆山东兄弟”。古人以九为阳数，九月九日也就是重阳节。写节日的诗，最忌落入俗套，这首诗高明就高明在避开俗套，把握住真性情。首句就用了两个“异”字，似乎脱口而出，却别有滋味地渲染出在异乡作客的生疏不适的孤独感。从“每逢”&nbsp; 二字来看，加倍的孤独感不是这个节日才有，以前的诸多节日已有过。正是孤独感久受压抑，于今尤甚的情形下，诗人把凝聚在内心深处的佳节思亲情结一吐为快。</p>', '0', '', 1, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-17 15:05:18', 'admin', '2024-12-01 15:42:51');
