INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (47, 100, '劝君更尽一杯酒，西出阳关无故人', '', 17, '办公室', '', '', '<p style=\"text-align: center;\">送元二使安西&nbsp;&nbsp;</p><p style=\"text-align: center;\">【唐】王维</p><p style=\"text-align: center;\">渭城朝雨浥轻尘，客舍青青柳色新。&nbsp;&nbsp;</p><p style=\"text-align: center; \">劝君更尽一杯酒，西出阳关无故人。&nbsp;&nbsp;</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">此诗前两句写送别的时间，地点，环境气氛。清晨，渭城客舍，自东向西一直延伸、不见尽头的驿道，客舍周围、驿道两旁的柳树。这一切，都仿佛是极平常的眼前景，读来却风光如画，抒情气氛浓郁。“朝雨”在这里扮演了一个重要的角色。早晨的雨下得不长，刚刚润湿尘土就停了。从长安西去的大道上，平日车马交驰，尘上飞扬，朝雨乍停，天气晴朗，道路显得洁净、清爽。“浥轻尘”的“浥”字是湿润的意思，在这里用得很有分寸，显出这雨澄尘而不湿路，恰到好处，仿佛天从人愿，特意为远行的人安排一条轻尘不扬的道路</p>', '0', '', 2, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-21 15:10:25', 'admin', '2024-12-01 16:19:02');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (48, 100, '枯藤老树昏鸦，小桥流水人家，古道西风瘦马', '', 18, '唐诗三百首', '', '', '<p style=\"text-align: center;\">天净沙·秋思&nbsp;&nbsp;</p><p style=\"text-align: center;\">【元】马致远</p><p style=\"text-align: center; \">枯藤老树昏鸦，小桥流水人家，古道西风瘦马。夕阳西下，断肠人在天涯。</p><p style=\"text-align: center; \"><br></p><p>这首小令很短，一共只有五句二十八个字，全曲无一秋字，但却描绘出一幅凄凉动人的秋郊夕照图，并且准确地传达出旅人凄苦的心境。被赞为秋思之祖这首成功的曲作，从多方面体现了中国古典歌的艺术特征。&nbsp;&nbsp;</p><p>一、以景托情，寓情于景，在景情的交融中构成一种凄凉悲苦的意境。&nbsp;&nbsp;</p><p>中国古典诗歌十分讲究意境的创造。意境是中国古典诗歌美学中的一个重要范畴，它的本质特征在于情景交融、心物合一。情与景能否妙合，成为能否构成意境的关键。清王夫之《萱斋诗话》曰：“情景名为二，而实不可离。神于诗者，妙合无垠。王国维《人间词话删稿》云：“一切景语皆情语也。</p>', '0', '', 3, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-20 15:10:25', 'admin', '2024-12-01 16:05:09');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (49, 100, '遥望洞庭山水翠，白银盘里一青螺', '', 32, '唐诗三百首', '', '', '<p style=\"text-align: center;\">望洞庭&nbsp;&nbsp;</p><p style=\"text-align: center;\">【唐】刘禹锡&nbsp;&nbsp;</p><p style=\"text-align: center;\">湖光秋月两相和，潭面无风镜未磨。&nbsp;&nbsp;</p><p style=\"text-align: center; \">遥望洞庭山水翠，白银盘里一青螺。</p><p style=\"text-align: center; \"><br></p><p>中描写了秋夜月光下洞庭湖的优美景色。微波不兴，平静秀美，分外怡人。诗人飞驰想像，以清新的笔调，生动地描绘出洞庭湖水宁静、祥和的朦胧美，勾画出一幅美丽的洞庭山水图。表现了诗人对大自然的热爱，也表现了诗人壮阔不凡的气度和高卓清奇的情致。&nbsp;&nbsp;</p><p>诗从一个“望”字着眼，“水月交融”、“湖平如镜”，是近望所见；“洞庭山水”、“犹如青螺”，是遥望所得。虽都是写望中景象，差异却显而易见。近景美妙、别致；远景迷潆、奇丽。潭面如镜，湖水如盘，君山如螺。银盘与青螺相映，明月与湖光互衬，更觉情景相容、相得益彰。</p>', '0', '', 5, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-19 15:10:25', 'admin', '2024-12-01 15:58:15');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (50, 100, '渭城朝雨浥轻尘，客舍青青柳色新。', '', 17, '办公室', '', '', '<p style=\"text-align: center; \">送元二使安西&nbsp;&nbsp;</p><p style=\"text-align: center; \">【作者】王维</p><p style=\"text-align: center;\">渭城朝雨浥轻尘，客舍青青柳色新。&nbsp;&nbsp;</p><p style=\"text-align: center; \">劝君更尽一杯酒，西出阳关无故人。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">此诗前两句写送别的时间，地点，环境气氛。清晨，渭城客舍，自东向西一直延伸、不见尽头的驿道，客舍周围、驿道两旁的柳树。这一切，都仿佛是极平常的眼前景，读来却风光如画，抒情气氛浓郁。“朝雨”在这里扮演了一个重要的角色。早晨的雨下得不长，刚刚润湿尘土就停了。从长安西去的大道上，平日车马交驰，尘上飞扬，朝雨乍停，天气晴朗，道路显得洁净、清爽。“浥轻尘”的“浥”字是湿润的意思，在这里用得很有分寸，显出这雨澄尘而不湿路，恰到好处，仿佛天从人愿，特意为远行的人安排一条轻尘不扬的道路。</p>', '0', '', 1, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-18 15:10:25', 'admin', '2024-12-01 15:32:06');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (51, 100, '白日依山尽，黄河入海流。欲穷千里目，更上一层楼。', '', 32, '办公室', '', '', '<p style=\"text-align: center; \"><span style=\"font-size: 24px;\">《登鹳雀楼》</span></p><p style=\"text-align: center; \">白日依山尽，黄河入海流。</p><p style=\"text-align: center; \">欲穷千里目，更上一层楼。</p><p><br></p><p>这首诗写诗人在登高望远中表现出来的不凡的胸襟抱负，反映了盛唐时期人们积极向上的进取精神。其中，前两句写所见。“白日依山尽”写远景，写山，写的是登楼望见的景色，“黄河入海流”写近景，写水写得景象壮观，气势磅礴。这里，诗人运用极其朴素、极其浅显的语言，既高度形象又高度概括地把进入广大视野的万里河山，收入短短十个字中；而后人在千载之下读到这十个字时，也如临其地，如见其景，感到胸襟为之一开。首句写遥望一轮落日向着楼前一望无际、连绵起伏的群山西沉，在视野的尽头冉冉而没。这是天空景、远方景、西望景。</p>', '0', '', 1, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-17 15:10:25', 'admin', '2024-12-01 11:18:03');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (52, 100, '初一少年培训蜕变记：结营盛典，见证不凡成长！', '', 2, '办公室', '', '', '<p style=\"text-align: center;\">金秋织锦，绘成长之画；&nbsp;&nbsp;</p><p style=\"text-align: center;\"><br></p><p style=\"text-align: center;\">培训砺志，铸青春之魂。&nbsp;&nbsp;</p><p style=\"text-align: center;\"><br></p><p style=\"text-align: center;\">高一和初一的新同学们，&nbsp;&nbsp;</p><p style=\"text-align: center;\"><br></p><p style=\"text-align: center;\">以汗为墨，笔走龙蛇书壮志；&nbsp;&nbsp;</p><p style=\"text-align: center;\"><br></p><p style=\"text-align: center; \">以坚为骨，心向苍穹展宏图。</p><p></p>', '0', '', 5, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-16 15:10:25', 'admin', '2024-09-17 21:34:15');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (53, 100, '碧玉妆成一树高，万条垂下绿丝绦。', '', 12, '办公室', '', '', '<p></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; font-size: 15px; color: rgb(51, 51, 51); line-height: 25px; margin-top: 5px; margin-bottom: 5px; font-family: Arial, sans-serif;\"><span id=\"body_1_0\" data=\"means_1_0\"><span class=\"body-zhushi-span\" data=\"&quot;\\u78a7\\u7eff\\u8272\\u7684\\u7389\\u3002\\u8fd9\\u91cc\\u7528\\u4ee5\\u6bd4\\u55bb\\u6625\\u5929\\u5ae9\\u7eff\\u7684\\u67f3\\u53f6\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">碧玉</span><span class=\"body-zhushi-span\" data=\"&quot;\\u88c5\\u9970\\uff0c\\u6253\\u626e\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">妆</span>成<span class=\"body-zhushi-span\" data=\"&quot;\\u6ee1\\u6811\\u3002\\u4e00\\uff1a\\u6ee1\\uff0c\\u5168\\u3002\\u5728\\u4e2d\\u56fd\\u53e4\\u5178\\u8bcd\\u548c\\u6587\\u7ae0\\u4e2d\\uff0c\\u6570\\u91cf\\u8bcd\\u5728\\u4f7f\\u7528\\u4e2d\\u5e76\\u4e0d\\u4e00\\u5b9a\\u8868\\u793a\\u786e\\u5207\\u7684\\u6570\\u91cf\\u3002\\u4e0b\\u4e00\\u53e5\\u7684\\u201c\\u4e07\\u201d\\uff0c\\u5c31\\u662f\\u8868\\u793a\\u5f88\\u591a\\u7684\\u610f\\u601d\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">一树</span>高，</span><span id=\"body_1_1\" data=\"means_1_1\">万条垂下绿丝<span class=\"body-zhushi-span\" data=\"&quot;\\u7528\\u4e1d\\u7f16\\u6210\\u7684\\u7ef3\\u5e26\\u3002\\u8fd9\\u91cc\\u6307\\u50cf\\u4e1d\\u5e26\\u4e00\\u6837\\u7684\\u67f3\\u6761\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">绦</span>。</span></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; font-size: 15px; color: rgb(51, 51, 51); line-height: 25px; margin-top: 5px; margin-bottom: 5px; font-family: Arial, sans-serif;\"><span id=\"body_2_0\" data=\"means_2_0\">不知细叶谁<span class=\"body-zhushi-span\" data=\"&quot;\\u88c1\\u526a\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">裁</span>出，</span><span id=\"body_2_1\" data=\"means_2_1\">二月春风<span class=\"body-zhushi-span\" data=\"&quot;\\u5982\\u540c\\uff0c\\u597d\\u50cf\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">似</span>剪刀。</span></p>', '0', '', 23, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-25 15:14:24', 'ry', '2024-09-17 18:00:37');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (54, 100, '小荷才露尖尖角，早有蜻蜓立上头。', '', 12, '办公室', '', '', '<p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; font-size: 15px; color: rgb(51, 51, 51); line-height: 25px; margin-top: 5px; margin-bottom: 5px; font-family: Arial, sans-serif;\"><span id=\"body_1_0\" data=\"means_1_0\"><span class=\"body-zhushi-span\" data=\"&quot;\\u78a7\\u7eff\\u8272\\u7684\\u7389\\u3002\\u8fd9\\u91cc\\u7528\\u4ee5\\u6bd4\\u55bb\\u6625\\u5929\\u5ae9\\u7eff\\u7684\\u67f3\\u53f6\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">碧玉</span><span class=\"body-zhushi-span\" data=\"&quot;\\u88c5\\u9970\\uff0c\\u6253\\u626e\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">妆</span>成<span class=\"body-zhushi-span\" data=\"&quot;\\u6ee1\\u6811\\u3002\\u4e00\\uff1a\\u6ee1\\uff0c\\u5168\\u3002\\u5728\\u4e2d\\u56fd\\u53e4\\u5178\\u8bcd\\u548c\\u6587\\u7ae0\\u4e2d\\uff0c\\u6570\\u91cf\\u8bcd\\u5728\\u4f7f\\u7528\\u4e2d\\u5e76\\u4e0d\\u4e00\\u5b9a\\u8868\\u793a\\u786e\\u5207\\u7684\\u6570\\u91cf\\u3002\\u4e0b\\u4e00\\u53e5\\u7684\\u201c\\u4e07\\u201d\\uff0c\\u5c31\\u662f\\u8868\\u793a\\u5f88\\u591a\\u7684\\u610f\\u601d\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">一树</span>高，</span><span id=\"body_1_1\" data=\"means_1_1\">万条垂下绿丝<span class=\"body-zhushi-span\" data=\"&quot;\\u7528\\u4e1d\\u7f16\\u6210\\u7684\\u7ef3\\u5e26\\u3002\\u8fd9\\u91cc\\u6307\\u50cf\\u4e1d\\u5e26\\u4e00\\u6837\\u7684\\u67f3\\u6761\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">绦</span>。</span></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; font-size: 15px; color: rgb(51, 51, 51); line-height: 25px; margin-top: 5px; margin-bottom: 5px; font-family: Arial, sans-serif;\"><span id=\"body_2_0\" data=\"means_2_0\">不知细叶谁<span class=\"body-zhushi-span\" data=\"&quot;\\u88c1\\u526a\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">裁</span>出，</span><span id=\"body_2_1\" data=\"means_2_1\">二月春风<span class=\"body-zhushi-span\" data=\"&quot;\\u5982\\u540c\\uff0c\\u597d\\u50cf\\u3002&quot;\" style=\"border-bottom: 1px solid rgb(38, 115, 219); cursor: pointer;\">似</span>剪刀。</span></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: left; font-size: 15px; color: rgb(51, 51, 51); line-height: 25px; margin-top: 5px; margin-bottom: 5px; font-family: Arial, sans-serif;\"><span id=\"body_2_1\" data=\"means_2_1\"><br></span></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: left; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">　　首句写树，柳树就像一位经过梳妆打扮的亭亭玉立的美人。柳，单单用碧玉来比有两层意思：一是碧玉这名字和柳的颜色有关，“碧”和下句的“绿”是互相生发、互为补充的。二是碧玉这个字在人们头脑中永远留下年轻的印象。“碧玉”二字用典而不露痕迹，南朝乐府有《碧玉歌》，其中“碧玉破瓜时”已成名句。还有南朝萧绎《采莲赋》有“碧玉小家女”，也很有名，后来形成“小家碧玉”这个成语。“碧玉妆成一树高”就自然地把眼前这棵柳树和那位古代质朴美丽的贫家少女联系起来，而且联想到她穿一身嫩绿，楚楚动人，充满青春活力。</span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: left; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\"><br></span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: left; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">　　故第二句就此联想到那垂垂下坠的柳叶就是...古诗文网&gt;&gt;</span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: left; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\"><br></span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: left; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">　　这是一首咏物诗，写的是早春二月的杨柳。</span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: left; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\"><br></span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span id=\"body_2_1\" data=\"means_2_1\" style=\"font-size: 15px;\"></span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: left; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">　　写杨柳，该从哪儿着笔呢？毫无疑问，它的形象美是在于那曼长披拂的枝条。一年一度，它长出了嫩绿的新叶，丝丝下垂，在春风吹拂中，有着一种迷人的意态。这是谁都能欣赏的。古典诗词中，借用这种形象美来形容、比拟美人苗条的身段，婀娜的腰支，也是我们所经常看到的。这诗别出新意，翻转过来。“碧玉妆成一树高”，一开始，杨柳就化身为美人而出现；“万条垂下绿丝绦”，这千条万缕的垂丝，也随之而变成了她的裙带。上句的“高”字，衬托出美人婷婷袅袅的风姿；下句的“垂”字，暗示出纤腰在风中款摆。诗中没有“杨柳”</span></font></p><p></p>', '0', '', 8, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-24 15:14:24', 'admin', '2024-09-17 17:26:47');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (55, 100, '春夜喜雨：好雨知时节，当春乃发生。    随风潜入夜，润物细无声。', '', 12, '办公室', '', '', '<p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">好雨知时节，当春乃发生。&nbsp;&nbsp;</span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">随风潜入夜，润物细无声。&nbsp;&nbsp;</span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">野径云俱黑，江船火独明。&nbsp;&nbsp;</span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">晓看红湿处，花重锦官城。</span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\"><br></span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">　　好雨似乎会挑选时节，降临在万物萌生之春。伴随和风，悄悄进入夜幕。细细密密，滋润大地万物。浓浓乌云，笼罩田野小路，唯有江边渔船上的一点渔火放射出一线光芒，显得格外明亮。等天亮的时候，那潮湿的泥土上必定布满了鲜艳的花瓣，锦官城的大街小巷也一定是一片万紫千红的景象。</span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\"><br></span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">　　这是描绘春夜雨景，表现喜悦心情的名作。一开头就用一个“好”字赞美“雨”。在生活里，“好”常常被用来赞美那些做好事的人。如今用“好”赞美雨，已经会唤起关于做好事的人的联想。接下去，就把雨拟人化，说它“知时节”，懂得满足客观需要。其中“知”字用得传神，简直把雨给写活了。春天是万物萌芽生长的季节，正需要下雨，雨就下起来了。它的确很“好”。</span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\"><br></span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">　　颔联写雨的“发生”，进一步表现雨的“好”，其中“潜”、“润”、“细”等字生动地写出了雨“好”的特点。雨之所以“好”，好就好在适时，好在“润物”。春天的雨，一般是伴随着和风细雨地滋润万物的。</span></font></p><p></p>', '0', '', 12, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-23 15:14:24', 'ry', '2024-09-17 18:05:20');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (56, 100, '冬虫夏草的主要功效为补肾益肺，止血化痰 ｜ 测试3', '', 12, '办公室', '', '', '<p>是</p>18', '0', '', 4, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'admin', '2024-05-22 15:14:24', 'ry', '2024-09-17 18:41:29');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (57, 100, '床前明月光，疑是地上霜。  举头望明月，低头思故乡。', '', 32, '办公室', '', '', '<p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">床前明月光，疑是地上霜。&nbsp;&nbsp;</span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">举头望明月，低头思故乡。&nbsp;&nbsp;</span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"text-align: center; line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\"><br></span></font></p><p class=\"poem-detail-main-text\" id=\"body_p\" style=\"line-height: 25px; margin-top: 5px; margin-bottom: 5px;\"><font color=\"#333333\" face=\"Arial, sans-serif\"><span style=\"font-size: 15px;\">这首诗写的是在寂静的月夜思念家乡的感受。诗的前两句，是写诗人在作客他乡的特定环境中一刹那间所产生的错觉。一个独处他乡的人，白天奔波忙碌，倒还能冲淡离愁，然而一到夜深人静的时候，心头就难免泛起阵阵思念故乡的波澜。何况是在月明之夜，更何况是月色如霜的秋夜。“疑是地上霜”中的“疑”字，生动地表达了诗人睡梦初醒，迷离恍惚中将照射在床前的清冷月光误作铺在地面的浓霜。而“霜”字用得更妙，既形容了月光的皎洁，又表达了季节的寒冷，还烘托出诗人飘泊他乡的孤寂凄凉之情。</span></font></p>', '0', '', 6, '0', '0', '0', '1', '1', '0', '0', NULL, '通过', 'admin', '2024-05-21 15:14:24', 'admin', '2024-12-01 11:15:53');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (58, 100, '大江东去，浪淘尽，千古风流人物', '', 18, '唐诗三百首', '', '', '<p style=\"text-align: center;\">念奴娇·赤壁怀古&nbsp;&nbsp;</p><p style=\"text-align: center;\">【宋】苏轼</p><p style=\"text-align: center;\">大江东去，浪淘尽，千古风流人物。&nbsp;&nbsp;</p><p style=\"text-align: center;\">故垒西边，人道是，三国周郎赤壁。&nbsp;&nbsp;</p><p style=\"text-align: center;\">乱石穿空，惊涛拍岸，卷起千堆雪。&nbsp;&nbsp;</p><p style=\"text-align: center;\">江山如画，一时多少豪杰。&nbsp;&nbsp;</p><p style=\"text-align: center;\">遥想公瑾当年，小乔初嫁了，雄姿英发。&nbsp;&nbsp;</p><p style=\"text-align: center;\">羽扇纶巾，谈笑间，樯橹灰飞烟灭。&nbsp;&nbsp;</p><p style=\"text-align: center;\">故国神游，多情应笑我，早生华发。&nbsp;&nbsp;</p><p style=\"text-align: center; \">人生如梦，一尊还酹江月。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">此词怀古抒情，写自己消磨壮心殆尽，转而以旷达之心关注历史和人生。上阕以描写赤壁矶风起浪涌的自然风景为主，意境开阔博大，感慨隐约深沉。起笔凌云健举，包举有力。将浩荡江流与千古人事并收笔下。千古风流人物既被大浪淘尽，则一己之微岂不可悲？然而苏轼却另有心得：既然千古风流人物也难免如此，那么一己之荣辱穷达复何足悲叹！人类既如此殊途而同归，则汲汲于一时功名，不免过于迂腐了。接下两句切入怀古主题，专说三国赤壁之事。“人道是“三字下得极有分寸。赤壁之战的故地，争议很大。一说在今湖北蒲圻县境内，已改为赤壁市。</p>', '0', '', 2, '1', '0', '0', '1', '1', '0', '0', NULL, '同意 ', 'admin', '2024-05-20 15:14:24', 'admin', '2024-12-01 16:07:05');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (59, 103, '过零丁洋：人生自古谁无死？留取丹心照汗青。', '公司的风格', 13, '办公室', '收拾收拾', '', '<p style=\"text-align: center;\">辛苦遭逢起一经，干戈寥落四周星。&nbsp;&nbsp;</p><p style=\"text-align: center;\">山河破碎风飘絮，身世浮沉雨打萍。&nbsp;&nbsp;</p><p style=\"text-align: center;\">惶恐滩头说惶恐，零丁洋里叹零丁。&nbsp;&nbsp;</p><p style=\"text-align: center; \">人生自古谁无死？留取丹心照汗青。</p><p>　　回想我早年由科举入仕历尽辛苦，如今战火消歇已熬过了四个年头。国家危在旦夕恰如狂风中的柳絮，个人又哪堪言说似骤雨里的浮萍。惶恐滩的惨败让我至今依然惶恐，零丁洋身陷元虏可叹我孤苦零丁。人生自古以来有谁能够长生不死？我要留一片爱国的丹心映照史册。</p><p><br></p><p>　　首联“辛苦遭逢起一经，干戈寥落四周星。”“起一经”当指天祥二十岁中进士说的，四周星即四年。天祥于德祐元年（1275），起兵勤王，至祥兴元年（1278）被俘，恰为四个年头。此自叙生平，思今忆昔。从时间说，拈出“入世”和“勤王”，一关个人出处，一关国家危亡，两件大事，一片忠心。唐宋时期，一个人要想替国家做出一番事业，必须入仕，要入仕，作为知识分子必须通过科举考选，考选就得读经文天祥遇难时，衣带中留有个自赞文说：“读圣贤书，所学何事，而今而后，庶几无愧”，就是把这两件事拴在一起的。圣人著作就叫经，经是治国安邦的。</p>', '0', '', 31, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'pcs', '2024-09-14 15:33:35', 'ry', '2024-09-16 20:40:05');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (60, 100, '在炎炎夏日的烈日下，我参加了一次难忘的培训', '', 12, '子部门', '培训还让我认识到了自己的不足之处。在遇到困难时，我曾想过放弃，但最终还是克服了这些消极情绪。我明白了自己的毅力和韧性还需要提高，但这也是一个成长的过程。通过这次培训，我更加明确了自己需要改进的地方，并将继续努力提高自己的能力。', '/profile/upload/2024/09/16/2002_20240916182941A001.jpg', '<p><br></p><p><br></p><p>　　在炎炎夏日的烈日下，我参加了一次难忘的培训。这不仅仅是一次身体上的挑战，更是一次心理和意志的考验。在这短暂而紧张的日子里，我学到了许多宝贵的经验，并对自己的能力有了更深刻的认识。</p><p style=\"text-align: center; \"><img src=\"/profile/upload/2024/09/16/2002_20240916182941A001.jpg\" style=\"width: 710px;\"><br></p><p>　　培训的日子里，我们每天按照严格的日程进行训练。早晨起床、整理宿舍、晨练，紧接着进行一整天的科学技能训练。虽然任务繁重，但正是这种充实的生活让我体验到了军人的严谨和自律。</p><p><br></p><p>　　在训练过程中，我深刻体会到了团队合作的重要性。我们需要与队友紧密配合，共同努力完成任务。此外，我也学会了如何坚持和克服困难。每当遇到挑战时，我告诉自己要坚强、不放弃，只有这样才能取得成功。</p><p><br></p><p>　　培训还让我认识到了自己的不足之处。在遇到困难时，我曾想过放弃，但最终还是克服了这些消极情绪。我明白了自己的毅力和韧性还需要提高，但这也是一个成长的过程。通过这次培训，我更加明确了自己需要改进的地方，并将继续努力提高自己的能力。</p><p><br></p><p>　　总之，这次培训让我深刻体会到了军人精神的内涵。它教会了我如何保持自律、坚定信念，并不断提升自己的能力。在未来的日子里，我将把这些经验教训牢记在心，并不断提高自己的素质和能力。</p>', '0', '', 33, '0', '1', '1', '1', '1', '0', '0', NULL, '', 'admin', '2024-09-16 18:30:17', 'admin', '2024-09-24 19:21:34');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (61, 103, '莫等闲、白了少年头，空悲切。', '', 15, '', '', '', '<p>怒发冲冠，凭栏处、潇潇雨歇。抬望眼、仰天长啸，壮怀激烈。三十功名尘与土，八千里路云和月。莫等闲、白了少年头，空悲切。&nbsp;&nbsp;</p><p>靖康耻，犹未雪。臣子恨，何时灭。驾长车，踏破贺兰山缺。壮志饥餐胡虏肉，笑谈渴饮匈奴血。待从头、收拾旧山河，朝天阙。</p><p><br></p><p><br></p><p>　　气得头发竖起，以至于将帽子顶起，登高倚栏杆，一场潇潇细雨刚刚停歇。抬头望眼四望辽阔一片，仰天长声啸叹，一片报国之心充满心怀。三十多年来虽已建立一些功名，但如同尘土微不足道，南北转战八千里，经过多少风云人生。不要虚度年华，花白了少年黑发，只有独自悔恨悲悲切切。靖康年的奇耻，尚未洗雪。臣子愤恨，何时才能泯灭。我要驾着战车向贺兰山进攻，连贺兰山也要踏为平地。我满怀壮志，打仗饿了就吃敌人的肉，谈笑渴了就喝敌人的鲜血。我要从头再来，收复旧日河山，朝拜故都京阙。<br></p>', '0', '', 10, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'ks', '2024-09-16 21:10:52', 'ry', '2024-09-16 21:11:36');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (62, 100, '满江红·写怀', '', 17, '', '', '/profile/upload/2024/09/16/2004_20240916213558A002.jpg', '<p>　　怒发冲冠，凭栏处、潇潇雨歇。抬望眼、仰天长啸，壮怀激烈。三十功名尘与土，八千里路云和月。莫等闲、白了少年头，空悲切。</p><p><br></p><p>　　靖康耻，犹未雪。臣子恨，何时灭。驾长车，踏破贺兰山缺。壮志饥餐胡虏肉，笑谈渴饮匈奴血。待从头、收拾旧山河，朝天阙。</p>', '0', '', 13, '0', '1', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-09-16 21:34:59', 'ry', '2024-09-17 14:12:37');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (63, 104, '解放战争的三个战略阶段（抗日战争的三个战略阶段）', '公司的风格', 16, '', '', '', '<p>　　大家好，近期很多朋友对于解放战争的三个战略阶段产不是很理解。然后还有一些网友想弄清楚抗日战争的三个战略阶段，泰缘号已经为你找到了相关问题的答案，接下来和我们一起看看吧，希望对大家有所帮助！</p><p><br></p><p>　　1947年5月，我国赤峰市的柴胡栏子村发生了一场震惊中央的遭遇战。</p><p><br></p><p>　　当时，我军的5名高级领导干部遭到了当地国民党残余部队和地方土匪的突袭！</p><p><br></p><p>　　面对这种情况，我军派来护送的警卫部队连长却不顾战友的性命逃之夭夭，这让革命同志遭受到了毁灭性打击。</p><p><br></p><p>　　事后，这件事报告给了中央，主席听闻警卫部队居然不顾战友的生命独自逃命。</p><p><br></p><p>　　当即勃然大怒，毛主席立即下令枪毙警卫连长和指导员。</p><p><br></p><p>　　那么，1947年5月这天，柴胡栏子村发生了什么事呢？警卫部队在战友遇难的时候为什么会临阵脱逃？</p>', '0', '', 7, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 11:30:00', 'ry', '2024-09-17 22:18:31');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (64, 103, '在大学的学习和生活中，也需要像在培训中一样', '', 18, '', '12', '/profile/upload/2024/09/17/102_20240917205841A001.jpg', '<p>　　培训生活的描述</p><p><br></p><p>　　训练内容：详细描述培训中的各项训练，如站军姿、踢正步、跑步等。“每天清晨，站军姿成为了开启新一天的必修课。我们在烈日下挺直脊梁，感受着汗水顺着脸颊滑落，尽管双腿酸痛，但依然努力保持着标准的姿势。”</p><p><br></p><p>　　作息时间：提及严格的作息安排，强调规律生活带来的变化。“培训的作息十分规律，早睡早起让我养成了良好的生活习惯，每天都充满活力。”</p><p><br></p><p>　　教官与同学：描写教官的严格要求和悉心指导，以及同学们之间的相互鼓励与支持。“教官的严厉中蕴含着关爱，他们用自己的专业和耐心，帮助我们不断进步。而同学们在训练中互相扶持，共同克服困难，这份情谊让我倍感温暖。”</p><p><br></p><p>　　个人成长与收获</p><p><br></p><p>　　身体素质提升：阐述培训对身体的锻炼，如耐力、力量、协调性等方面的进步。“经过这段时间的培训，我的身体素质有了显著提升，不再是那个一运动就气喘吁吁的自己。”</p><p><br></p><p>　　意志品质磨砺：强调在培训中培养的坚韧不拔、吃苦耐劳、自律等品质。“面对高强度的训练和恶劣的天气，我学会了坚持，懂得了只有克服困难才能不断成长。”</p><p><br></p><p>　　团队意识增强：讲述在集体训练中体会到的团队合作的重要性。“在队列训练和拔河比赛等活动中，我深刻认识到团队的力量。只有大家齐心协力，才能取得好的成绩。”</p><p><br></p><p>　　对大学生活的启示</p><p><br></p><p>　　联系培训经历，思考如何将在培训中培养的品质运用到大学生活中。“培训让我明白，在大学的学习和生活中，也需要像在培训中一样，保持坚韧的意志、良好的团队合作精神和自律的习惯。”</p><p><br></p><p>　　表达对未来大学生活的期待和决心。“我将以培训为起点，积极面对未来的挑战，努力成为更好的自己。”</p>', '0', '', 7, '1', '0', '0', '1', '1', '0', '0', NULL, '同意', 'ks', '2024-09-17 20:58:43', 'admin', '2024-12-01 16:25:26');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (65, 104, '《南京大屠杀》：“遗忘大屠杀就是二次屠杀”，用生命记录中国世纪浩劫', '', 16, '办公室', '', '/profile/upload/2024/09/17/103_20240917215648A001.jpg', '<p>　　南京大屠杀，不仅是南京这座城市，更是中华民族永久难以愈合的精神创伤，也是中国人民永远不能忘却的苦难史。与犹太人在欧洲所遭受的大屠杀举世皆知不同，南京大屠杀一直鲜为人知。在《南京大屠杀》作者、美籍华裔女作家张纯如看来，这是因为南京大屠杀的受害者一直保持沉默。受害者沉默的背后，是美、英等国家对日本的纵容，他们试图替日本掩饰罪恶。张纯如详细记载了日本在南京的暴行，以及世界为保留南京大屠杀记忆作了怎样的努力和斗争。<br></p>', '0', '', 6, '1', '0', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 21:57:48', 'ry', '2024-09-26 11:29:35');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (66, 104, '七七事变，又称卢沟桥事变', '', 16, '', '', '/profile/upload/2024/09/17/104_20240917220045A002.jpg', '<p>　　七七事变，又称卢沟桥事变，是日军于1937年7月7日在宛平县发动的一场侵略袭击！七七事变又标志着日本帝国主义，向中国发动了全面的侵华战争，同时也标志着中国全民族抗日的开端，由此开辟了，世界反法西斯战争的东方战场。向为保家卫国，所有牺牲的英烈英雄致敬！勿忘国耻!<br></p>', '0', '', 2, '1', '1', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 22:00:48', 'ry', '2024-09-26 11:29:15');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (68, 102, '悲壮的抗争与辉煌的胜利：中国近现代战争的历史回顾 ', '', 16, '', '', '/profile/upload/2024/09/17/106_20240917221615A004.jpg', '<p>　　中国近现代战争的历史，是一部充满悲壮与抗争的史诗。从鸦片战争到抗日战争，中国人民在抗击外侮、争取民族独立和人民解放的伟大斗争中，付出了巨大的代价，也取得了辉煌的胜利。</p><p><br></p><p>　　1840年，鸦片战争爆发，英国以贩卖鸦片为借口，对中国发动侵略战争。清政府在腐败无能的情况下，签订了《南京条约》，使中国沦为半殖民地半封建社会。</p><p><br></p><p>　　1894年，中日甲午战争爆发。清政府在战争中的腐败和无能，导致了北洋舰队的全军覆没。战后签订的《马关条约》，使中国丧失了更多的领土和主权。</p><p><br></p><p>　　1931年，日本发动九一八事变，侵占了中国东北。1937年，卢沟桥事变爆发，日本全面侵华。在民族危亡的关头，中国人民团结一心，共赴国难，进行了长达八年的抗日战争。经过艰苦卓绝的斗争，1945年，中国人民终于赢得了抗日战争的伟大胜利。</p><p><br></p><p>　　在这些战争中，中国人民展现出了顽强的抗争精神和不屈的意志。他们用自己的血肉之躯，筑起了保卫家园的钢铁长城。他们用简陋的武器，对抗着武装到牙齿的敌人。他们用生命和鲜血，谱写了一曲曲惊天地、泣鬼神的英雄赞歌。</p><p><br></p><p>　　在这些战争中，涌现出了无数可歌可泣的英雄人物。有在鸦片战争中英勇抗击英军的关天培；有在甲午战争中为国捐躯的邓世昌；有在抗日战争中英勇牺牲的张自忠、赵登禹等。</p><p><br></p><p>　　回顾中国近现代战争的历史，我们深感和平的珍贵和不易。今天的中国，已经不再是那个任人宰割的“东亚病夫”。</p><p><br></p><p>　　但是，我们不能忘记历史，更不能忘记那些为国家独立和民族解放而英勇献身的先烈们。我们应该铭记历史，珍爱和平，发扬抗战精神，为实现中华民族伟大复兴的中国梦而努力奋斗。</p>', '0', '', 9, '1', '1', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 22:13:12', 'ry', '2024-09-26 11:28:40');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (69, 104, '作为当年全球著名的驻外记者，福尔曼被称为“现代马可波罗”', '', 16, '', '', '', '<p>　　1944年6月，美国合众社、伦敦泰晤士报的著名战地记者哈里森·福尔曼突破国民党层层封锁，来到革命圣地延安。哈里森·福尔曼，美国著名摄影记者、探险家、作家和美国地理学会会员。</p><p><br></p><p>　　作为当年全球著名的驻外记者，福尔曼被称为“现代马可波罗”，早在上世纪30年代初他便来到中国西藏，他拍摄了大量关于西藏风土人情的照片。1937年抗战全面爆发后，福尔曼立即转换报道方向，他来到上海，对这里进行了广泛报道。</p>', '0', '', 9, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 22:23:17', 'ry', '2024-09-17 22:27:16');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (70, 104, '福尔曼发现中国共产党领导的敌后抗日武装力量', '', 16, '', '', '', '<p>　　随着报道的深入，福尔曼发现中国共产党领导的敌后抗日武装力量，在抗战中发挥着重要作用，但国民党却对延安地区进行全面封锁，不允许对其进行客观报道。与此同时，因为国民队伍腐败，导致国统区老百姓民怨沸腾。</p><p><br></p><p>　　跟国统区相比，中共领导的抗日根据地却蒸蒸日上，老百姓抗日热情高涨。在这样的背景下，无数外国记者都要求去延安地区进行实地采访。起初，国民党当局直接拒绝了，最终蒋介石迫于舆论压力，只能勉强同意，但严格规定了记者数量。</p>', '0', '', 12, '0', '0', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 22:23:57', 'ry', '2024-09-17 22:27:27');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (71, 104, '中外记者西北参观团”抵达延安，福尔曼成为其中一员', '', 16, '', '', '/profile/upload/2024/09/17/107_20240917222610A005.jpg', '<p>　　1944年6月9日，“中外记者西北参观团”抵达延安，福尔曼成为其中一员，他们受到中共方面最为隆重的欢迎。为了让外国记者们全面了解中共情况，延安交际处组织中外记者参观了边区的机关、学校、工厂和乡村等。</p><p><br></p><p>　　当时毛主席等人指示，只要“中外记者西北参观团”提出了要求，都要尽量满足。正如此，我们看到福尔曼拍摄了大量照片，包括跟中共领导人、八路军高级将领、八路军战士、民兵等的合影。</p>', '0', '', 11, '0', '1', '0', '1', '1', '0', '0', NULL, '同意', 'de', '2024-09-17 22:25:05', 'admin', '2024-09-24 22:56:58');
