INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (74, 102, '恰同学少年，风华正茂；书生意气，挥斥方遒。', '', 22, '', '在深秋一个秋高气爽的日子里，眺望着湘江碧水缓缓北流。我独自伫立在橘子洲头。看万千山峰全都变成了鲜艳，一层层树林好像染过颜色一样，江水清澈澄碧，一艘艘大船乘风破浪，争先恐后。', '/profile/upload/2024/10/04/a3f4c8c9b1d7842547b84fc5e2e7f775_20241004220507A001.jpeg', '<p style=\"text-align: center; \">独立寒秋，湘江北去，橘子洲头。&nbsp;&nbsp;</p><p style=\"text-align: center; \">看万山红遍，层林尽染；漫江碧透，百舸争流。&nbsp;&nbsp;</p><p style=\"text-align: center; \">鹰击长空，鱼翔浅底，万类霜天竞自由。&nbsp;&nbsp;</p><p style=\"text-align: center; \">怅寥廓，问苍茫大地，谁主沉浮？&nbsp;&nbsp;</p><p style=\"text-align: center; \">携来百侣曾游，忆往昔峥嵘岁月稠。&nbsp;&nbsp;</p><p style=\"text-align: center; \">恰同学少年，风华正茂；书生意气，挥斥方遒。&nbsp;&nbsp;</p><p style=\"text-align: center; \">指点江山，激扬文字，粪土当年万户侯。&nbsp;&nbsp;</p><p style=\"text-align: center; \">曾记否，到中流击水，浪遏飞舟？</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">在深秋一个秋高气爽的日子里，眺望着湘江碧水缓缓北流。我独自伫立在橘子洲头。看万千山峰全都变成了鲜艳，一层层树林好像染过颜色一样，江水清澈澄碧，一艘艘大船乘风破浪，争先恐后。广阔的天空里鹰在矫健有力地飞，鱼在清澈的水里轻快地游着，万物都在秋光中争着过自由自在的生活。面对着无边无际的宇宙，（千万种思绪一齐涌上心头）我要问：这苍茫大地的盛衰兴废，由谁来决定主宰呢?回想过去，我和我的同学，经常携手结伴来到这里游玩。在一起商讨国家大事，那无数不平凡的岁月至今还萦绕在我的心头。同学们正值青春年少，风华正茂；大家踌躇满志，意气奔放，正强劲有力。评论国家大事，写出这些激浊扬清的文章，把当时那些军阀官僚看得如同粪土。可曾记得，那时我们在江水深急的地方游泳，那激起的浪花几乎挡住了疾驰而来的船?&nbsp;&nbsp;<br></p>', '0', '', 20, '1', '0', '0', '1', '1', '0', '0', NULL, '同意', 'ks', '2024-09-24 23:56:19', 'ry', '2024-10-04 22:19:41');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (75, 100, '春望：国破山河在，城春草木深。', '', 21, '原创', '', '', '<p style=\"text-align: center; \">国破山河在，城春草木深。&nbsp;&nbsp;</p><p style=\"text-align: center; \">感时花溅泪，恨别鸟惊心。&nbsp;&nbsp;</p><p style=\"text-align: center; \">烽火连三月，家书抵万金。&nbsp;&nbsp;</p><p style=\"text-align: center; \">白头搔更短，浑欲不胜簪。</p><p style=\"text-align: center; \"><br></p><p>　　“国破山河在，城春草木深。”篇一开头描写了春望所见：山河依旧，可是国都已经沦陷，城池也在战火中残破不堪了，乱草丛生，林木荒芜。诗人记忆中昔日长安的春天是何等的繁华，鸟语花香，飞絮弥漫，烟柳明媚，游人迤逦，可是那种景象今日已经荡然无存了。一个“破”字使人怵目惊心，继而一个“深”字又令人满目凄然。诗人写今日景物，实为抒发人去物非的历史感，将感情寄寓于物，借助景物反托情感，为全诗创造了一片荒凉凄惨的气氛。“国破”和“城春”两个截然相反的意象，同时存在并形成强烈的反差。“城春”当指春天花草树木繁盛茂密，烟景明丽的季节，可是由于“国破”</p><p><br></p><p>　　唐玄宗天宝十五年（７５６）七月，安史叛军攻陷长安，肃宗在灵武即位，改元至德。杜甫在投奔灵武途中，被叛军俘至长安，次年（至德二年）写此诗。</p><p><br></p><p>　　诗人目睹沦陷后的长安之箫条零落，身历逆境思家情切，不免感慨万端。诗的一、二两联，写春城败象，饱含感叹；三、四两联写心念亲人境况，充溢离情。</p><p><br></p><p>　　全诗沉着蕴藉，真挚自然，反映了诗人热爱祖国，眷怀家人的感情。今人徐应佩、周溶泉等评此诗曰：“意脉贯通而平直，情景兼备而不游离，感情强烈而不浅露，内容丰富而不芜杂，格律严谨而不板滞。”此论颇为妥帖。“家书抵万金”亦为流传千古之名言。</p>', '0', '', 5, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-09-25 14:08:58', 'admin', '2024-09-25 14:09:04');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (76, 100, '秋夜将晓出篱门迎凉有感', '', 22, '办公室', '', '', '<p style=\"text-align: center; \">　　迢迢天汉西南落，喔喔邻鸡一再鸣。</p><p style=\"text-align: center; \">　　壮志病来消欲尽，出门搔首怆平生。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">　　陆游（1125—1210），字务观，号放翁。汉族，越州山阴（今浙江绍兴）人，南宋著名诗人。少时受家庭爱国思想熏陶，高宗时应礼部试，为秦桧所黜。孝宗时赐进士出身。中年入蜀，投身军旅生活，官至宝章阁待制。晚年退居家乡。其一生笔耕不辍，今存九千多首，内容极为丰富。与王安石、苏轼、黄庭坚并称“宋代四大诗人”，又与杨万里、范成大、尤袤合称“南宋四大家”。著有《剑南诗稿》、《渭南文集》、《南唐书》、《老学庵笔记》等。<br></p>', '0', '', 7, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-09-25 14:11:05', 'admin', '2024-09-25 14:11:20');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (77, 100, '十一月四日风雨大作（其二）', '', 21, '', '', '', '<p style=\"text-align: center; \">僵卧孤村不自哀，尚思为国戍轮台。</p><p style=\"text-align: center; \">夜阑卧听风吹雨，铁马冰河入梦来。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">　　风卷江湖雨暗村，四山声作海涛翻。溪柴火软蛮毡暖，我与狸奴不出门。僵卧孤村不自哀，尚思为国戍轮台。夜阑卧听风吹雨，铁马冰河入梦来。第一首诗主要写十一月四日的大雨和诗人之处境。前两句以夸张之法写大雨瓢泼，其声响之巨，描绘出黑天大风大雨之境，很是生动，波涛汹涌之声正与作者渴望为国出力、光复中原之心相印。后两句转写近处，描写其所处之境，写出作者因天冷而不思出门，其妙处是把作者的主观之感和猫结合一起写。第二首诗以“痴情化门”的手法，深沉地表达了作者收复国土、报效祖国的壮志和那种“年既老而不衰”的矢志不渝精神，向读者展示了诗人的一片赤胆忠心<br></p>', '0', '', 2, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-09-25 14:12:51', 'admin', '2024-09-25 14:12:55');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (78, 100, '破阵子·为陈同甫赋壮词以寄之', '', 21, '', '', '', '<p style=\"text-align: center; \">醉里挑灯看剑，梦回吹角连营。八百里分麾下炙，五十弦翻塞外声，沙场秋点兵。&nbsp;&nbsp;</p><p style=\"text-align: center; \">马作的卢飞快，弓如霹雳弦惊。了却君王天下事，赢得生前身后名。可怜白发生！</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">　　此词以两个二、二、二的对句开头，通过具体、生动的描述，表现了多层情意。第一句，只六个字，却用三个连续的、富有特征性的动作，塑造了一个壮士的形象，让读者从那些动作中去体会人物的内心活动，去想象人物所处的环境，意味无穷。为什么要吃酒，而且吃“醉”？既“醉”之后，为什么不去睡觉，而要“挑灯”？“挑”亮了“灯”，为什么不干别的，偏偏抽出宝剑，映着灯光看了又看？……这一连串问题，只要细读全词，就可能作出应有的回答，因而不必说明。“此时无声胜有声”。用什么样的“说明”也难以比这无言的动作更有力地展现人物的内心世界。<br></p>', '0', '', 6, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-09-25 14:14:21', 'admin', '2024-09-25 14:14:32');
