INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (80, 102, '假如钟声响了 就请用羽毛 把我安葬 我将在冥夜中', '', 22, '', '', '', '<p style=\"text-align: center; \">　　假如钟声响了</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \">　　就请用羽毛</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \">　　把我安葬</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \">　　我将在冥夜中</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \">　　编织一对</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \">　　巨大的翅膀</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \">　　在我眷恋的祖国上空</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \">　　继续飞翔</p>', '0', '', 6, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-09-25 15:03:43', 'admin', '2024-09-25 15:11:26');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (81, 102, '泊秦淮:烟笼寒水月笼沙，夜泊秦淮近酒家。', '', 21, '', '', '', '<p style=\"text-align: center; \">烟笼寒水月笼沙，夜泊秦淮近酒家。&nbsp;&nbsp;</p><p style=\"text-align: center; \">商女不知亡国恨，隔江犹唱后庭花。</p><p><br></p><p>　　这首诗是即景感怀的，金陵曾是六朝都城，繁华一时。目睹如今的唐朝国势日衰，当权者昏庸荒淫，不免要重蹈六朝覆辙，无限感伤。首句写景，先竭力渲染水边夜色的清淡素雅；二句叙事，点明夜泊地点；三、四句感怀，由“近酒家”引出商女之歌，酒家多有歌妓，自然洒脱；由歌曲之靡靡，牵出“不知亡国恨”，抨击豪绅权贵沉溺于声色，含蓄深沉；由“亡国恨”推出“后庭花”的曲调，借陈后主之死，鞭笞权贵的荒淫，深刻犀利。<br></p>', '0', '', 5, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-09-25 15:11:21', 'admin', '2024-09-25 15:11:43');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (82, 100, '呜呼！楚虽三户能亡秦，岂有堂堂中国空无人！', '', 23, '办公室', '', '', '<p style=\"text-align: center; \">黄金错刀白玉装，夜穿窗扉出光芒。&nbsp;&nbsp;</p><p style=\"text-align: center; \">丈夫五十功未立，提刀独立顾八荒。&nbsp;&nbsp;</p><p style=\"text-align: center; \">京华结交尽奇士，意气相期共生死。&nbsp;&nbsp;</p><p style=\"text-align: center; \">千年史册耻无名，一片丹心报天子。&nbsp;&nbsp;</p><p style=\"text-align: center; \">尔来从军天汉滨，南山晓雪玉嶙峋。&nbsp;&nbsp;</p><p style=\"text-align: center; \">呜呼！楚虽三户能亡秦，岂有堂堂中国空无人！</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">　　这首诗在描绘具体形象时着墨不多，却起到了重要的点染作用。“黄金错刀白玉装”一句，描绘宝刀外观之美。黄金装饰的刀身，白玉镶嵌的刀柄，金玉相映，华美无比。“夜穿窗扉出光芒”一句，描绘宝刀内质之佳。它在黑夜中光芒四射，竟可穿透窗户，确实不凡。这两句，从诗歌意脉上说，是托物起兴，通过对宝刀的描绘和赞美，自然引出下面的提刀人形象。而从内在意蕴上说，又对提刀人的身份和志趣起着一种映衬作用，实质暗示了宝刀无用武之地，英雄无报国之门。“丈夫五十功未立，提刀独立顾八荒。”这里有三层意思：“提刀独立”写提刀人的动作，显出其急欲上阵杀敌<br></p>', '0', '', 9, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-09-25 15:14:32', 'admin', '2024-09-25 15:44:38');
