INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (105, 100, '饮湖上初晴后雨二首·其二  ', '', 23, '唐诗三百首', '', '', '<p style=\"text-align: center; \">饮湖上初晴后雨二首·其二&nbsp;&nbsp;</p><p style=\"text-align: center; \">【宋】苏轼&nbsp;&nbsp;</p><p style=\"text-align: center; \">水光潋滟晴方好，山色空蒙雨亦奇。&nbsp;&nbsp;</p><p style=\"text-align: center; \">欲把西湖比西子，淡妆浓抹总相宜。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">诗的上半首既写了西湖的水光山色，也写了西湖的晴姿雨态。“水光潋滟晴方好”描写西湖晴天的水光：在灿烂的阳光照耀下，西湖水波荡漾，波光闪闪，十分美丽。“山色空蒙雨亦奇”描写雨天的山色：在雨幕笼罩下，西湖周围的群山，迷迷茫茫，若有若无，非常奇妙。从第一首诗可知，这一天诗人陪着客人在西湖游宴终日，早晨阳光明艳，后来转阴，入暮后下起雨来。而在善于领略自然并对西湖有深厚感情的诗人眼中，无论是水是山，或晴或雨，都是美好奇妙的。从“晴方好”“雨亦奇”这一赞评，可以想见在不同天气下的湖山胜景，也可想见诗人即景挥毫时的兴会及其洒脱的性格、开阔的胸怀。</p>', '0', '', 0, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-12-01 17:30:57', 'admin', '2024-12-01 17:31:03');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (106, 100, '举头望明月，低头思故乡。', '', 23, '唐诗三百首', '', '', '<p style=\"text-align: center; \">静夜思&nbsp;&nbsp;</p><p style=\"text-align: center; \">【唐】李白</p><p style=\"text-align: center; \">床前明月光，疑是地上霜。&nbsp;&nbsp;</p><p style=\"text-align: center; \">举头望明月，低头思故乡。</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">这首诗写的是在寂静的月夜思念家乡的感受。诗的前两句，是写诗人在作客他乡的特定环境中一刹那间所产生的错觉。一个独处他乡的人，白天奔波忙碌，倒还能冲淡离愁，然而一到夜深人静的时候，心头就难免泛起阵阵思念故乡的波澜。何况是在月明之夜，更何况是月色如霜的秋夜。“疑是地上霜”中的“疑”字，生动地表达了诗人睡梦初醒，迷离恍惚中将照射在床前的清冷月光误作铺在地面的浓霜。而“霜”字用得更妙，既形容了月光的皎洁，又表达了季节的寒冷，还烘托出诗人飘泊他乡的孤寂凄凉之情。</p>', '0', '', 1, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-12-01 17:33:52', 'admin', '2024-12-01 17:33:56');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (107, 100, '春风又绿江南岸，明月何时照我还', '', 18, '', '', '', '<p style=\"text-align: center; \">泊船瓜洲&nbsp;&nbsp;</p><p style=\"text-align: center; \">【宋】王安石</p><p style=\"text-align: center; \">京口瓜洲一水间，钟山只隔数重山。&nbsp;&nbsp;</p><p style=\"text-align: center; \">春风又绿江南岸，明月何时照我还？</p><p style=\"text-align: center; \"><br></p><p>这是一首著名的抒情小诗，抒发了诗人眺望江南、思念家乡的深切感情。本诗从字面上看，是流露着对故乡的怀念之情，大有急欲飞舟渡江回家和亲人团聚的愿望。其实，在字里行间也寓着他重返政治舞台、推行新政的强烈欲望。&nbsp;&nbsp;</p><p>诗人回首江南，大地一片翠绿，这固然是春风吹绿的，但是那葱绿的禾苗难道不是变法措施产生的实效吗？&nbsp;&nbsp;</p><p>但是官场是险象环生的，诗人望着这瓜洲渡口，也望着钟山的明月，发出了“明月何时照我还”的慨叹，诗人是想早点离开是非黑白颠倒的官场，离开那丑恶，腐朽的地方体现作者希望重返那没有利益纷争的家乡，很有余韵。</p>', '0', '', 1, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'admin', '2024-12-01 17:37:35', 'admin', '2024-12-01 17:37:39');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (108, 101, '小楼昨夜又东风，故国不堪回首月明中', '', 22, '', '', '', '<p style=\"text-align: center; \">虞美人·春花秋月何时了&nbsp;&nbsp;</p><p style=\"text-align: center; \">【五代十国】李煜【朝代】拼音译文&nbsp;&nbsp;</p><p style=\"text-align: center; \">春花秋月何时了？往事知多少。小楼昨夜又东风，故国不堪回首月明中。&nbsp;&nbsp;</p><p style=\"text-align: center; \">雕栏玉砌应犹在，只是朱颜改。问君能有几多愁？恰似一江春水向东流。</p><p style=\"text-align: left;\"><br></p><p style=\"text-align: left;\">《虞美人》李煜的代表作，也是李后主的绝命词。相传他于自己生日（七月七日）之夜（“七夕”），在寓所命歌妓作乐，唱新作《虞美人》词，声闻于外。宋太宗闻之大怒，命人赐药酒，将他毒死。这首词通过今昔交错对比，表现了一个亡国之君的无穷的哀怨。&nbsp;&nbsp;</p><p style=\"text-align: left;\">“春花秋月何时了，往事知多少！”三春花开，中秋月圆，岁月不断更替，人生多么美好。可我这囚犯的苦难岁月，什么时候才能完结呢？“春花秋月何时了”表明词人身为阶下囚，怕春花秋月勾起往事而伤怀。回首往昔，身为国君，过去许许多多的事到底做得如何呢，怎么会弄到今天这步田地？</p>', '0', '', 0, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'ry', '2024-12-01 17:42:13', 'ry', '2024-12-01 17:42:18');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (109, 101, '一个深夜的记忆', '', 23, '百度百科', '', '', '<p style=\"text-align: center; \">一个深夜的记忆&nbsp;&nbsp;</p><p style=\"text-align: center; \">【作者】鲁藜【朝代】现代</p><p style=\"text-align: center; \">月光流进门槛&nbsp;&nbsp;</p><p style=\"text-align: center; \">我以为是阳光&nbsp;&nbsp;</p><p style=\"text-align: center; \">开门，还是深夜&nbsp;&nbsp;</p><p style=\"text-align: center; \">不久，有风从北边来&nbsp;&nbsp;</p><p style=\"text-align: center; \">仿佛吹动了月亮的弓弦&nbsp;&nbsp;</p><p style=\"text-align: center; \">于是我听见了黎明的音响&nbsp;&nbsp;</p><p style=\"text-align: center; \">河岸被山影压着&nbsp;&nbsp;</p><p style=\"text-align: center; \">有星流过旷野去&nbsp;&nbsp;</p><p style=\"text-align: center; \">我感觉到，万物还在沉睡&nbsp;&nbsp;</p><p style=\"text-align: center; \">只有我是最初醒来的人</p><p style=\"text-align: center; \"><br></p><p style=\"text-align: center; \"><br></p><p style=\"text-align: left;\">宁静而深沉的意境&nbsp;&nbsp;</p><p style=\"text-align: left;\">曾卓&nbsp;&nbsp;</p><p style=\"text-align: left;\">人们谈论鲁藜的诗，一般都没有提到这一首，而我对它却很有偏爱。&nbsp;&nbsp;</p><p style=\"text-align: left;\">这首诗写于抗战时期的延安，描写的是诗人在一个深夜醒来时所看到的情景。&nbsp;&nbsp;</p><p style=\"text-align: left;\">从门槛下流进的月光，诗人误以为是曙光。从梦中乍然醒来，是容易有这样的错觉的，李白就曾将床前的月光误以为是霜。他起来了，推开门看看，原来还是深夜。可以想见，他是习惯于黎明即起的。是他惊醒的是对黎明的期待。</p>', '0', '', 1, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'ry', '2024-12-01 17:44:33', 'ry', '2024-12-01 17:44:40');
INSERT INTO `sys_portal_article` (`article_id`, `dept_id`, `article_title`, `subhead`, `category_id`, `source`, `description`, `thumbnail`, `content`, `art_type`, `art_skip`, `degree_num`, `is_push`, `is_slide`, `is_top`, `check_status`, `is_issue`, `show_status`, `is_grant`, `grant_id`, `remark`, `create_by`, `create_time`, `update_by`, `update_time`) VALUES (110, 101, '我的灵魂变得像河流一般的深邃。', '', 23, '百度百科', '', '', '<p style=\"text-align: center; \">黑人谈河流&nbsp;&nbsp;</p><p style=\"text-align: center; \">【作者】休斯【朝代】现代拼音&nbsp;&nbsp;</p><p style=\"text-align: center; \">我了解河流：&nbsp;&nbsp;</p><p style=\"text-align: center; \">我了解像世界一样的古老的河流，&nbsp;&nbsp;</p><p style=\"text-align: center; \">比人类血管中流动的血液更古老的河流。&nbsp;&nbsp;</p><p style=\"text-align: center; \">我的灵魂变得像河流一般的深邃。&nbsp;&nbsp;</p><p style=\"text-align: center; \">晨曦中我在幼发拉底河沐浴，&nbsp;&nbsp;</p><p style=\"text-align: center; \">在刚果河畔我盖了一间茅舍，&nbsp;&nbsp;</p><p style=\"text-align: center; \">河水潺潺催我入眠。&nbsp;&nbsp;</p><p style=\"text-align: center; \">我瞰望尼罗河，在河畔建造了金字塔。&nbsp;&nbsp;</p><p style=\"text-align: center; \">当林肯去新奥尔良时，&nbsp;&nbsp;</p><p style=\"text-align: center; \">我听到密西西比河的歌声，&nbsp;&nbsp;</p><p style=\"text-align: center; \">我瞧见它那浑浊的胸膛&nbsp;&nbsp;</p><p style=\"text-align: center; \">在夕阳下闪耀的金光。&nbsp;&nbsp;</p><p style=\"text-align: center; \">我了解河流：&nbsp;&nbsp;</p><p style=\"text-align: center; \">古老的黝黑的河流。&nbsp;&nbsp;</p><p style=\"text-align: center; \">我的灵魂变得像河流一般深邃。</p>', '0', '', 1, '0', '0', '0', '1', '1', '0', '0', NULL, '', 'ry', '2024-12-01 17:46:13', 'ry', '2024-12-01 17:46:18');
